  <style>
    /* ÂºïÂÖ•Â≠ó‰Ωì */
    @import url('https://fonts.googleapis.com/css2?family=M+PLUS+Rounded+1c:wght@400;700&family=Noto+Sans+SC:wght@400;700&display=swap');

    /* ÈÄâÊã©Ê°ÜÂÆπÂô® */
    .choice-container {
