    .choice-toggle::after {
      right: 20px; /* Èù†ËøëÊñáÊú¨ */
    }

    /* ËèúÂçïÂÆπÂô® */
    .choice-menu {
      display: none;
      background: linear-gradient(to bottom, #2C2A4A, #5B4B84); /* Ê∑±Á¥´Ê∏êÂèò */
      border: 2px solid #8A6EAF; /* ËæÉ‰∫ÆÁöÑÁ¥´Ëâ≤ËæπÊ°Ü */
      border-radius: 16px;
