      box-shadow: 0 4px 15px rgba(147, 112, 219, 0.2); /* Á¥´Ëâ≤ËæâÂÖâ */
      animation: bounceIn 0.4s ease-out;
      position: relative; /* ‰∏∫ÊòüÊòüÂÆö‰Ωç */
    }

    /* ËèúÂçï‰∏ãÊñπÁöÑÊòüÊòü */
    .choice-menu::before {
      content: '‚ú¶'; /* ÊòüÊòü */
      position: absolute;
      bottom: 3px; /* ÊãâÂºÄË∑ùÁ¶ª */
      left: 50%;
      transform: translateX(-50%);
      font-size: 15px;
      color: #8A6EAF;
      animation: starFlash 1.2s infinite;
      z-index: 10; /* Á°Æ‰øù‰∏çË¢´Ë¶ÜÁõñ */
    }

    /* ËèúÂçïÂ±ïÂºÄÂä®Áîª */
    @keyframes bounceIn {
      0% {
        opacity: 0;
        transform: scale(0.8) translateY(-20px);
      }
      60% {
        transform: scale(1.05);
      }
      100% {
        opacity: 1;
        transform: scale(1) translateY(0);
      }
